import React, { useState } from 'react';
import { Mail, Lock, User, Calendar, Eye, EyeOff, Award } from 'lucide-react';

const ModernLoginSignup = () => {
    const [action, setAction] = useState("Login");
    const [formData, setFormData] = useState({
        name: "",
        email: "",
        password: "",
        dob: "",
        role: "",
        medicalLicense: ""
    });
    const [errorMsg, setErrorMsg] = useState("");
    const [isLoading, setIsLoading] = useState(false);
    const [showPassword, setShowPassword] = useState(false);

    const handleInputChange = (field, value) => {
        setFormData(prev => ({ ...prev, [field]: value }));
        if (errorMsg) setErrorMsg("");
    };

    const handleLogin = async () => {
        setErrorMsg("");
        setIsLoading(true);

        // Simulate API call
        setTimeout(() => {
            if (formData.email && formData.password) {
                alert("Login successful! (This is a demo)");
            } else {
                setErrorMsg("Please fill in all fields");
            }
            setIsLoading(false);
        }, 1500);
    };

    const handleSignUp = async () => {
        setErrorMsg("");
        setIsLoading(true);

        if (!formData.name || !formData.email || !formData.password || !formData.dob || !formData.role) {
            setErrorMsg("All fields are required");
            setIsLoading(false);
            return;
        }

        if (formData.role === "doctor" && !formData.medicalLicense) {
            setErrorMsg("Doctor registration requires a medical license number.");
            setIsLoading(false);
            return;
        }

        // Simulate API call
        setTimeout(() => {
            alert("SignUp successful! Please login. (This is a demo)");
            setAction("Login");
            setFormData({
                name: "",
                email: "",
                password: "",
                dob: "",
                role: "",
                medicalLicense: ""
            });
            setIsLoading(false);
        }, 1500);
    };

    const handleSubmit = () => {
        if (action === "Login") handleLogin();
        else handleSignUp();
    };

    return (
        <div className="min-h-screen bg-gradient-to-br from-slate-900 via-purple-900 to-slate-900 flex items-center justify-center p-4 relative overflow-hidden">
            {/* Animated background elements */}
            <div className="absolute inset-0 overflow-hidden">
                <div className="absolute -top-40 -right-32 w-80 h-80 bg-purple-500 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-pulse"></div>
                <div className="absolute -bottom-40 -left-32 w-80 h-80 bg-cyan-500 rounded-full mix-blend-multiply filter blur-xl opacity-70 animate-pulse animation-delay-2000"></div>
                <div className="absolute top-1/2 left-1/2 transform -translate-x-1/2 -translate-y-1/2 w-96 h-96 bg-indigo-500 rounded-full mix-blend-multiply filter blur-xl opacity-50 animate-pulse animation-delay-4000"></div>
            </div>

            {/* Glassmorphism container */}
            <div className="relative w-full max-w-md">
                <div className="backdrop-blur-xl bg-white/10 border border-white/20 rounded-3xl p-8 shadow-2xl">
                    {/* Header */}
                    <div className="text-center mb-8">
                        <div className="inline-block p-3 bg-gradient-to-r from-purple-500 to-cyan-500 rounded-2xl mb-4 shadow-lg">
                            <User className="w-8 h-8 text-white" />
                        </div>
                        <h1 className="text-3xl font-bold bg-gradient-to-r from-white to-gray-300 bg-clip-text text-transparent mb-2">
                            {action}
                        </h1>
                        <div className="w-20 h-1 bg-gradient-to-r from-purple-500 to-cyan-500 rounded-full mx-auto"></div>
                    </div>

                    {/* Form */}
                    <div className="space-y-5">
                        {action === "SignUp" && (
                            <>
                                {/* Name Input */}
                                <div className="relative group">
                                    <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                                        <User className="h-5 w-5 text-gray-400 group-focus-within:text-purple-400 transition-colors" />
                                    </div>
                                    <input
                                        type="text"
                                        placeholder="Full Name"
                                        value={formData.name}
                                        onChange={(e) => handleInputChange('name', e.target.value)}
                                        className="w-full pl-10 pr-4 py-3 bg-white/5 border border-white/10 rounded-xl text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500/50 focus:border-purple-500/50 transition-all duration-300 backdrop-blur-sm"
                                    />
                                </div>

                                {/* Date of Birth */}
                                <div className="relative group">
                                    <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                                        <Calendar className="h-5 w-5 text-gray-400 group-focus-within:text-purple-400 transition-colors" />
                                    </div>
                                    <input
                                        type="date"
                                        value={formData.dob}
                                        onChange={(e) => handleInputChange('dob', e.target.value)}
                                        className="w-full pl-10 pr-4 py-3 bg-white/5 border border-white/10 rounded-xl text-white focus:outline-none focus:ring-2 focus:ring-purple-500/50 focus:border-purple-500/50 transition-all duration-300 backdrop-blur-sm"
                                    />
                                </div>

                                {/* Role Selection */}
                                <div className="relative">
                                    <select
                                        value={formData.role}
                                        onChange={(e) => handleInputChange('role', e.target.value)}
                                        className="w-full px-4 py-3 bg-white/5 border border-white/10 rounded-xl text-white focus:outline-none focus:ring-2 focus:ring-purple-500/50 focus:border-purple-500/50 transition-all duration-300 backdrop-blur-sm appearance-none cursor-pointer"
                                    >
                                        <option value="" className="bg-slate-800">Select Role</option>
                                        <option value="patient" className="bg-slate-800">Patient</option>
                                        <option value="doctor" className="bg-slate-800">Doctor</option>
                                        <option value="family" className="bg-slate-800">Family Member</option>
                                    </select>
                                    <div className="absolute inset-y-0 right-0 flex items-center px-2 pointer-events-none">
                                        <svg className="w-4 h-4 text-gray-400" fill="none" stroke="currentColor" viewBox="0 0 24 24">
                                            <path strokeLinecap="round" strokeLinejoin="round" strokeWidth="2" d="M19 9l-7 7-7-7"></path>
                                        </svg>
                                    </div>
                                </div>

                                {/* Medical License (for doctors) */}
                                {formData.role === "doctor" && (
                                    <div className="relative group">
                                        <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                                            <Award className="h-5 w-5 text-gray-400 group-focus-within:text-purple-400 transition-colors" />
                                        </div>
                                        <input
                                            type="text"
                                            placeholder="Medical License Number"
                                            value={formData.medicalLicense}
                                            onChange={(e) => handleInputChange('medicalLicense', e.target.value)}
                                            className="w-full pl-10 pr-4 py-3 bg-white/5 border border-white/10 rounded-xl text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500/50 focus:border-purple-500/50 transition-all duration-300 backdrop-blur-sm"
                                        />
                                    </div>
                                )}
                            </>
                        )}

                        {/* Email Input */}
                        <div className="relative group">
                            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                                <Mail className="h-5 w-5 text-gray-400 group-focus-within:text-purple-400 transition-colors" />
                            </div>
                            <input
                                type="email"
                                placeholder="Email Address"
                                value={formData.email}
                                onChange={(e) => handleInputChange('email', e.target.value)}
                                className="w-full pl-10 pr-4 py-3 bg-white/5 border border-white/10 rounded-xl text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500/50 focus:border-purple-500/50 transition-all duration-300 backdrop-blur-sm"
                            />
                        </div>

                        {/* Password Input */}
                        <div className="relative group">
                            <div className="absolute inset-y-0 left-0 pl-3 flex items-center pointer-events-none">
                                <Lock className="h-5 w-5 text-gray-400 group-focus-within:text-purple-400 transition-colors" />
                            </div>
                            <input
                                type={showPassword ? "text" : "password"}
                                placeholder="Password"
                                value={formData.password}
                                onChange={(e) => handleInputChange('password', e.target.value)}
                                className="w-full pl-10 pr-12 py-3 bg-white/5 border border-white/10 rounded-xl text-white placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500/50 focus:border-purple-500/50 transition-all duration-300 backdrop-blur-sm"
                            />
                            <button
                                type="button"
                                onClick={() => setShowPassword(!showPassword)}
                                className="absolute inset-y-0 right-0 pr-3 flex items-center text-gray-400 hover:text-purple-400 transition-colors"
                            >
                                {showPassword ? <EyeOff className="h-5 w-5" /> : <Eye className="h-5 w-5" />}
                            </button>
                        </div>
                    </div>

                    {/* Error Message */}
                    {errorMsg && (
                        <div className="mt-4 p-3 bg-red-500/20 border border-red-500/30 rounded-xl text-red-300 text-sm text-center backdrop-blur-sm">
                            {errorMsg}
                        </div>
                    )}

                    {/* Forgot Password (Login only) */}
                    {action === "Login" && (
                        <div className="mt-4 text-center">
                            <button className="text-sm text-gray-400 hover:text-purple-400 transition-colors">
                                Forgot your password?
                            </button>
                        </div>
                    )}

                    {/* Submit Button */}
                    <div className="mt-8">
                        <button
                            onClick={handleSubmit}
                            disabled={isLoading}
                            className="w-full py-3 px-6 bg-gradient-to-r from-purple-600 to-cyan-600 hover:from-purple-700 hover:to-cyan-700 text-white font-semibold rounded-xl shadow-lg hover:shadow-xl transform hover:scale-105 transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed disabled:transform-none focus:outline-none focus:ring-2 focus:ring-purple-500/50"
                        >
                            {isLoading ? (
                                <div className="flex items-center justify-center">
                                    <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin mr-2"></div>
                                    Processing...
                                </div>
                            ) : (
                                action
                            )}
                        </button>
                    </div>

                    {/* Switch Action */}
                    <div className="mt-6 text-center">
                        <p className="text-gray-400">
                            {action === "Login" ? "Don't have an account? " : "Already have an account? "}
                            <button
                                onClick={() => setAction(action === "Login" ? "SignUp" : "Login")}
                                className="text-purple-400 hover:text-purple-300 font-medium transition-colors"
                            >
                                {action === "Login" ? "Sign Up" : "Log In"}
                            </button>
                        </p>
                    </div>
                </div>

                {/* Decorative elements */}
                <div className="absolute -z-10 top-0 left-0 w-full h-full bg-gradient-to-br from-purple-500/10 to-cyan-500/10 rounded-3xl blur-3xl"></div>
            </div>
        </div>
    );
};

export default ModernLoginSignup;
