import React, { useState, useEffect, useCallback, useRef } from 'react';
import { Menu, X, Home, Calendar, User, LogOut, Settings, Bell, ChevronLeft } from 'lucide-react';

// Mock API functions (replace with your actual API calls)
const mockAPI = {
  getAvailableSlots: async (doctorId, date) => {
    await new Promise(resolve => setTimeout(resolve, 500));
    return ['09:00:00', '09:30:00', '10:00:00', '10:30:00', '11:00:00', '14:00:00', '14:30:00', '15:00:00'];
  },
  reserveSlot: async (userId, doctorId, dateTime) => {
    await new Promise(resolve => setTimeout(resolve, 300));
    return { expires_in: 300 };
  },
  confirmSlot: async (userId, doctorId, dateTime) => {
    await new Promise(resolve => setTimeout(resolve, 300));
    return { success: true };
  },
  cancelSlot: async (userId, doctorId, dateTime) => {
    await new Promise(resolve => setTimeout(resolve, 300));
    return { success: true };
  }
};

const DEFAULT_DOCTOR_ID = 3;
const DEFAULT_USER_ID = 1;

const getTodayDateString = () => {
  return new Date().toISOString().split('T')[0];
};

const Sidebar = ({ isOpen, setIsOpen }) => {
  const sidebarItems = [
    { icon: Home, label: 'Dashboard', href: '#dashboard' },
    { icon: Calendar, label: 'My Appointments', href: '#appointments' },
    { icon: User, label: 'Profile', href: '#profile' },
    { icon: Bell, label: 'Notifications', href: '#notifications' },
    { icon: Settings, label: 'Settings', href: '#settings' },
  ];

  const handleNavigation = (href, label) => {
    console.log(`Navigating to: ${label}`);
    // Add your navigation logic here
  };

  const handleLogout = () => {
    if (window.confirm('Are you sure you want to logout?')) {
      console.log('Logging out...');
      // Add your logout logic here
    }
  };

  return (
    <>
      {/* Mobile Overlay */}
      {isOpen && (
        <div 
          className="fixed inset-0 bg-black bg-opacity-50 z-40 lg:hidden"
          onClick={() => setIsOpen(false)}
        />
      )}

      {/* Sidebar */}
      <div className={`
        fixed top-0 left-0 h-full bg-slate-900/95 backdrop-blur-xl border-r border-white/10
        transition-transform duration-300 ease-in-out z-50
        ${isOpen ? 'translate-x-0' : '-translate-x-full'}
        lg:translate-x-0 w-64
      `}>
        {/* Sidebar Header */}
        <div className="p-6 border-b border-white/10">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-3">
              <div className="w-10 h-10 bg-gradient-to-br from-blue-500 to-purple-600 rounded-lg flex items-center justify-center">
                <Calendar className="w-6 h-6 text-white" />
              </div>
              <div>
                <h2 className="text-lg font-semibold text-white">MediBook</h2>
                <p className="text-xs text-slate-400">Healthcare Portal</p>
              </div>
            </div>
            <button 
              onClick={() => setIsOpen(false)}
              className="lg:hidden p-2 hover:bg-white/10 rounded-lg transition-colors"
            >
              <X className="w-5 h-5 text-slate-400" />
            </button>
          </div>
        </div>

        {/* User Profile Section */}
        <div className="p-6 border-b border-white/10">
          <div className="flex items-center gap-3">
            <div className="w-12 h-12 bg-gradient-to-br from-green-500 to-blue-600 rounded-full flex items-center justify-center">
              <User className="w-6 h-6 text-white" />
            </div>
            <div>
              <p className="text-sm font-medium text-white">John Doe</p>
              <p className="text-xs text-slate-400">Patient ID: {DEFAULT_USER_ID}</p>
            </div>
          </div>
        </div>

        {/* Navigation Items */}
        <nav className="flex-1 p-4">
          <ul className="space-y-2">
            {sidebarItems.map((item) => (
              <li key={item.label}>
                <button
                  onClick={() => handleNavigation(item.href, item.label)}
                  className="w-full flex items-center gap-3 px-4 py-3 text-slate-300 hover:text-white hover:bg-white/10 rounded-lg transition-all duration-200 group"
                >
                  <item.icon className="w-5 h-5 group-hover:scale-110 transition-transform" />
                  <span className="font-medium">{item.label}</span>
                </button>
              </li>
            ))}
          </ul>
        </nav>

        {/* Logout Button */}
        <div className="p-4 border-t border-white/10">
          <button
            onClick={handleLogout}
            className="w-full flex items-center gap-3 px-4 py-3 text-red-400 hover:text-red-300 hover:bg-red-500/10 rounded-lg transition-all duration-200 group"
          >
            <LogOut className="w-5 h-5 group-hover:scale-110 transition-transform" />
            <span className="font-medium">Logout</span>
          </button>
        </div>
      </div>
    </>
  );
};

const AppointmentBooking = () => {
  const [sidebarOpen, setSidebarOpen] = useState(false);
  const [doctorId, setDoctorId] = useState(DEFAULT_DOCTOR_ID);
  const [date, setDate] = useState(getTodayDateString());
  const [userId, setUserId] = useState(DEFAULT_USER_ID);
  const [slots, setSlots] = useState([]);
  const [reservedSlots, setReservedSlots] = useState({});
  const [selectedTime, setSelectedTime] = useState(null);
  const [ownReservedKey, setOwnReservedKey] = useState(null);
  const [status, setStatus] = useState({ message: '', type: null });
  const [showModal, setShowModal] = useState(false);
  const [loading, setLoading] = useState(false);

  const showStatus = useCallback((message, isError = false) => {
    setStatus({ message, type: isError ? 'error' : 'success' });
    setTimeout(() => {
      setStatus({ message: '', type: null });
    }, 5000);
  }, []);

  const handleLoadSlots = async () => {
    if (!doctorId || !date || !userId) {
      showStatus('Please fill in all fields', true);
      return;
    }

    setLoading(true);
    try {
      const availableTimes = await mockAPI.getAvailableSlots(doctorId, date);
      setSlots(availableTimes);
      setReservedSlots({});
      setSelectedTime(null);
      setOwnReservedKey(null);
      setShowModal(false);
      showStatus(`Loaded ${availableTimes.length} available slots`);
    } catch (error) {
      console.error('Failed to load slots:', error);
      setSlots([]);
      showStatus('Failed to load slots', true);
    }
    setLoading(false);
  };

  const handleSlotClick = (time) => {
    const key = `${doctorId}:${date}T${time}`;

    if (reservedSlots[key] && ownReservedKey !== key) {
      showStatus('This slot is already reserved by another user', true);
      return;
    }

    if (ownReservedKey === key) {
      setShowModal(true);
      return;
    }

    setSelectedTime(time === selectedTime ? null : time);
  };

  const handleReserveSlot = async () => {
    if (!selectedTime) {
      showStatus('Please select a slot first', true);
      return;
    }

    const appointmentDateTime = `${date}T${selectedTime}`;
    setLoading(true);

    try {
      const result = await mockAPI.reserveSlot(userId, doctorId, appointmentDateTime);
      const reservedKey = `${doctorId}:${appointmentDateTime}`;
      setOwnReservedKey(reservedKey);
      setReservedSlots(prev => ({ ...prev, [reservedKey]: true }));
      showStatus(`Slot reserved! Expires in ${result.expires_in} seconds`);
      setSelectedTime(null);
      setShowModal(true);
    } catch (error) {
      console.error('Failed to reserve slot:', error);
      showStatus('Failed to reserve slot', true);
    }
    setLoading(false);
  };

  const handleConfirmSlot = async () => {
    if (!ownReservedKey) {
      showStatus('No reserved slot to confirm', true);
      return;
    }

    setLoading(true);
    try {
      const [docId, dateTime] = ownReservedKey.split(':');
      await mockAPI.confirmSlot(userId, docId, dateTime);
      showStatus('Booking confirmed successfully! ðŸŽ‰');
      setOwnReservedKey(null);
      setShowModal(false);
      setTimeout(handleLoadSlots, 1000);
    } catch (error) {
      console.error('Failed to confirm slot:', error);
      showStatus('Failed to confirm booking', true);
    }
    setLoading(false);
  };

  const handleCancelSlot = async () => {
    if (!ownReservedKey) {
      showStatus('No reserved slot to cancel', true);
      return;
    }

    if (!window.confirm('Are you sure you want to cancel this reservation?')) return;

    setLoading(true);
    try {
      const [docId, dateTime] = ownReservedKey.split(':');
      await mockAPI.cancelSlot(userId, docId, dateTime);
      showStatus('Reservation cancelled successfully');
      setOwnReservedKey(null);
      setShowModal(false);
      setTimeout(handleLoadSlots, 1000);
    } catch (error) {
      console.error('Failed to cancel slot:', error);
      showStatus('Failed to cancel reservation', true);
    }
    setLoading(false);
  };

  const selectedSlotTime = ownReservedKey ? ownReservedKey.split('T')[1].substring(0, 5) : selectedTime;

  return (
    <div className="min-h-screen bg-gradient-to-br from-slate-900 via-slate-800 to-slate-900">
      {/* Sidebar */}
      <Sidebar isOpen={sidebarOpen} setIsOpen={setSidebarOpen} />

      {/* Main Content */}
      <div className="lg:ml-64 transition-all duration-300">
        {/* Top Header */}
        <header className="bg-slate-900/90 backdrop-blur-xl border-b border-white/10 px-6 py-4">
          <div className="flex items-center justify-between">
            <div className="flex items-center gap-4">
              <button
                onClick={() => setSidebarOpen(true)}
                className="lg:hidden p-2 hover:bg-white/10 rounded-lg transition-colors"
              >
                <Menu className="w-6 h-6 text-white" />
              </button>
              <div className="flex items-center gap-3">
                <button className="flex items-center gap-2 px-3 py-2 text-slate-300 hover:text-white hover:bg-white/10 rounded-lg transition-all">
                  <ChevronLeft className="w-4 h-4" />
                  <span className="text-sm font-medium">Back to Dashboard</span>
                </button>
              </div>
            </div>
            <h1 className="text-xl font-semibold text-white flex items-center gap-3">
              <Calendar className="w-6 h-6 text-blue-400" />
              Book Appointment
            </h1>
          </div>
        </header>

        {/* Hero Section */}
        <div className="bg-gradient-to-r from-blue-600/20 to-purple-600/20 backdrop-blur-sm border-b border-white/10 px-6 py-8">
          <div className="max-w-4xl mx-auto text-center">
            <h2 className="text-3xl font-bold text-white mb-2 flex items-center justify-center gap-3">
              <span className="text-4xl">ðŸ©º</span>
              Schedule Your Consultation
            </h2>
            <p className="text-slate-300">Book your appointment with our expert healthcare professionals</p>
          </div>
        </div>

        {/* Main Content */}
        <div className="p-6 max-w-6xl mx-auto">
          {/* Booking Form */}
          <div className="bg-slate-800/50 backdrop-blur-xl border border-white/10 rounded-2xl p-6 mb-6">
            <h3 className="text-xl font-semibold text-white mb-6">Appointment Details</h3>

            <div className="grid md:grid-cols-3 gap-6 mb-6">
              <div>
                <label className="block text-sm font-medium text-slate-300 mb-2">
                  <User className="w-4 h-4 inline mr-2" />
                  Doctor ID
                </label>
                <input
                  type="number"
                  value={doctorId}
                  onChange={(e) => setDoctorId(parseInt(e.target.value) || '')}
                  className="w-full px-4 py-3 bg-slate-900/50 border border-white/10 rounded-lg text-white focus:border-blue-500 focus:ring-2 focus:ring-blue-500/20 transition-all"
                  placeholder="Enter doctor ID"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-slate-300 mb-2">
                  <Calendar className="w-4 h-4 inline mr-2" />
                  Date
                </label>
                <input
                  type="date"
                  value={date}
                  onChange={(e) => setDate(e.target.value)}
                  className="w-full px-4 py-3 bg-slate-900/50 border border-white/10 rounded-lg text-white focus:border-blue-500 focus:ring-2 focus:ring-blue-500/20 transition-all"
                />
              </div>

              <div>
                <label className="block text-sm font-medium text-slate-300 mb-2">
                  <User className="w-4 h-4 inline mr-2" />
                  User ID
                </label>
                <input
                  type="number"
                  value={userId}
                  onChange={(e) => setUserId(parseInt(e.target.value) || '')}
                  className="w-full px-4 py-3 bg-slate-900/50 border border-white/10 rounded-lg text-white focus:border-blue-500 focus:ring-2 focus:ring-blue-500/20 transition-all"
                  placeholder="Enter user ID"
                />
              </div>
            </div>

            <div className="flex justify-center">
              <button
                onClick={handleLoadSlots}
                disabled={loading}
                className="px-6 py-3 bg-gradient-to-r from-blue-600 to-purple-600 hover:from-blue-700 hover:to-purple-700 text-white font-medium rounded-lg transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed flex items-center gap-2"
              >
                {loading ? (
                  <>
                    <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                    Loading...
                  </>
                ) : (
                  <>
                    <Calendar className="w-4 h-4" />
                    Load Available Slots
                  </>
                )}
              </button>
            </div>
          </div>

          {/* Time Slots */}
          <div className="bg-slate-800/50 backdrop-blur-xl border border-white/10 rounded-2xl p-6 mb-6">
            <div className="flex items-center justify-between mb-6">
              <h3 className="text-xl font-semibold text-white flex items-center gap-2">
                <span className="text-2xl">â°</span>
                Available Time Slots
              </h3>
              {slots.length > 0 && (
                <span className="px-3 py-1 bg-green-600 text-white text-sm font-medium rounded-full">
                  {slots.length} slots available
                </span>
              )}
            </div>

            {slots.length === 0 ? (
              <div className="text-center py-12">
                <div className="text-6xl mb-4 opacity-50">ðŸ“‹</div>
                <p className="text-slate-400">Click "Load Available Slots" to see available appointments</p>
              </div>
            ) : (
              <div className="grid grid-cols-2 sm:grid-cols-3 md:grid-cols-4 lg:grid-cols-6 gap-3">
                {slots.map((time) => {
                  const key = `${doctorId}:${date}T${time}`;
                  const isReserved = !!reservedSlots[key];
                  const isSelected = selectedTime === time;
                  const isOwnReserved = ownReservedKey === key;

                  let className = "p-4 text-center rounded-lg border-2 transition-all duration-300 cursor-pointer relative overflow-hidden group ";

                  if (isOwnReserved) {
                    className += "bg-green-600 border-green-500 text-white shadow-lg shadow-green-600/25";
                  } else if (isReserved) {
                    className += "bg-red-600/20 border-red-500 text-red-300 cursor-not-allowed opacity-60";
                  } else if (isSelected) {
                    className += "bg-blue-600 border-blue-500 text-white shadow-lg shadow-blue-600/25 transform scale-105";
                  } else {
                    className += "bg-slate-900/50 border-white/10 text-slate-300 hover:border-blue-500 hover:bg-blue-600/10 hover:text-white hover:scale-105";
                  }

                  return (
                    <div
                      key={time}
                      className={className}
                      onClick={() => handleSlotClick(time)}
                    >
                      <div className="font-semibold text-sm">{time}</div>
                      {isOwnReserved && <div className="text-xs mt-1 opacity-80">Your Hold</div>}
                      {isReserved && !isOwnReserved && <div className="text-xs mt-1">Reserved</div>}
                    </div>
                  );
                })}
              </div>
            )}
          </div>

          {/* Action Buttons */}
          <div className="flex flex-col sm:flex-row gap-4 justify-center">
            <button
              onClick={handleReserveSlot}
              disabled={!selectedTime || !!ownReservedKey || loading}
              className="px-6 py-3 bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 text-white font-medium rounded-lg transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
            >
              <Calendar className="w-4 h-4" />
              Reserve Selected Slot
            </button>

            <button
              onClick={() => setShowModal(true)}
              disabled={!ownReservedKey}
              className="px-6 py-3 bg-slate-700 hover:bg-slate-600 border border-white/20 text-white font-medium rounded-lg transition-all duration-300 disabled:opacity-50 disabled:cursor-not-allowed flex items-center justify-center gap-2"
            >
              <Settings className="w-4 h-4" />
              Manage Reserved Slot
            </button>
          </div>
        </div>
      </div>

      {/* Status Toast */}
      {status.message && (
        <div className={`fixed top-4 right-4 px-6 py-4 rounded-lg text-white font-medium shadow-lg backdrop-blur-sm z-50 transform transition-all duration-300 ${
          status.type === 'success' 
            ? 'bg-green-600/90 border border-green-500/50' 
            : 'bg-red-600/90 border border-red-500/50'
        }`}>
          <div className="flex items-center gap-2">
            <span className="text-lg">{status.type === 'success' ? 'âœ…' : 'âš ï¸'}</span>
            {status.message}
          </div>
        </div>
      )}

      {/* Confirmation Modal */}
      {showModal && (
        <div className="fixed inset-0 bg-black/50 backdrop-blur-sm flex items-center justify-center z-50 p-4">
          <div className="bg-slate-800 border border-white/10 rounded-2xl p-6 max-w-md w-full">
            <div className="flex items-center justify-between mb-4">
              <h3 className="text-xl font-semibold text-white">Manage Reservation</h3>
              <button
                onClick={() => setShowModal(false)}
                className="p-2 hover:bg-white/10 rounded-lg transition-colors"
              >
                <X className="w-5 h-5 text-slate-400" />
              </button>
            </div>

            <div className="text-center mb-6">
              <div className="flex items-center justify-center gap-3 mb-4 p-4 bg-blue-600/10 border border-blue-500/20 rounded-lg">
                <Calendar className="w-8 h-8 text-blue-400" />
                <span className="text-2xl font-bold text-blue-400">{selectedSlotTime}</span>
              </div>
              <p className="text-slate-300">Your slot is reserved. What would you like to do?</p>
            </div>

            <div className="flex flex-col gap-3">
              <button
                onClick={handleConfirmSlot}
                disabled={loading}
                className="w-full px-4 py-3 bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 text-white font-medium rounded-lg transition-all duration-300 disabled:opacity-50 flex items-center justify-center gap-2"
              >
                {loading ? (
                  <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                ) : (
                  <>
                    <span>âœ…</span>
                    Confirm Booking
                  </>
                )}
              </button>

              <button
                onClick={handleCancelSlot}
                disabled={loading}
                className="w-full px-4 py-3 bg-gradient-to-r from-red-600 to-red-700 hover:from-red-700 hover:to-red-800 text-white font-medium rounded-lg transition-all duration-300 disabled:opacity-50 flex items-center justify-center gap-2"
              >
                {loading ? (
                  <div className="w-4 h-4 border-2 border-white/30 border-t-white rounded-full animate-spin" />
                ) : (
                  <>
                    <span>âŒ</span>
                    Cancel Reservation
                  </>
                )}
              </button>

              <button
                onClick={() => setShowModal(false)}
                className="w-full px-4 py-3 bg-slate-700 hover:bg-slate-600 border border-white/20 text-white font-medium rounded-lg transition-all duration-300"
              >
                Close
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default AppointmentBooking;
