import React, { useEffect, useState, useCallback } from "react";
import { useNavigate } from "react-router-dom";
// Ensure these functions exist in your API service
import { getCurrentUser, getFamilyMembers, logoutUser } from "../../services/api"; 
import "./family_dashboard.css";

export default function FamilyDashboard() {
    const [user, setUser] = useState({ name: "", email: "" });
    const [family, setFamily] = useState([]);
    const [loading, setLoading] = useState(true);
    const [error, setError] = useState("");
    const navigate = useNavigate();
    const token = sessionStorage.getItem("token");

    // Use useCallback for async function to prevent unnecessary re-creation
    const loadData = useCallback(async () => {
        if (!token) {
            navigate("/login");
            return;
        }

        try {
            setLoading(true);
            setError("");

            // 1. Get current user info
            const currentUser = await getCurrentUser(token);
            setUser(currentUser);

            // 2. Get family members list
            const familyMembers = await getFamilyMembers(token);
            setFamily(familyMembers);

        } catch (err) {
            console.error("Failed to load user or family data:", err);
            setError("Failed to load family data. Please try again.");

            // On API failure, clear session and force re-login only if it's an auth error
            if (err.status === 401 || err.status === 403) {
                sessionStorage.clear();
                alert("Session expired. Please log in again.");
                navigate("/login");
            }
        } finally {
            setLoading(false);
        }
    }, [token, navigate]);

    useEffect(() => {
        loadData();

        // Cleanup: Use the function returned by useEffect to handle any needed cleanup
        return () => {
            // Optional: Any cleanup logic here
        };
    }, [loadData]); // loadData is stable because it's wrapped in useCallback

    const handleLogout = () => {
        if (window.confirm("Are you sure you want to logout?")) {
            // Assuming logoutUser() clears server session/cookies if necessary
            try {
                logoutUser(); 
            } catch (err) {
                console.error("Logout error:", err);
            }
            sessionStorage.clear(); // Ensure local storage is cleared
            navigate("/login");
        }
    };

    // Function to handle navigation to the invitations page
    const handleViewInvitations = () => {
        navigate("/family/invitations"); 
    };

    const handleSendInvite = () => {
        navigate("/family/send-invite");
    };

    if (loading) {
        return (
            <div className="dashboard-page">
                <div style={{
                    display: 'flex',
                    flexDirection: 'column',
                    justifyContent: 'center',
                    alignItems: 'center',
                    minHeight: '100vh',
                    color: '#E2E8F0',
                    fontSize: '18px',
                    gap: '20px'
                }}>
                    <div style={{
                        width: '50px',
                        height: '50px',
                        border: '4px solid #334155',
                        borderTop: '4px solid #3B82F6',
                        borderRadius: '50%',
                        animation: 'spin 1s linear infinite'
                    }}></div>
                    <div>Loading your family dashboard...</div>
                </div>
            </div>
        );
    }

    return (
        <div className="dashboard-page">
            <header>
                <h2>{user.name ? `${user.name}'s Family Dashboard` : 'Family Dashboard'}</h2>
            </header>
            <div className="container">

                <div className="card welcome-card">
                    <h3>Welcome, {user.name || 'User'}</h3>
                    <p><strong>Email:</strong> {user.email || 'Not available'}</p>
                    <p><strong>Role:</strong> Family Member</p>
                    <p style={{ 
                        marginTop: '15px', 
                        fontSize: '14px', 
                        color: '#94A3B8',
                        fontStyle: 'italic'
                    }}>
                        Manage your family connections and stay connected with your loved ones
                    </p>
                </div>

                {error && (
                    <div className="card" style={{
                        background: 'linear-gradient(135deg, #DC2626, #B91C1C)',
                        border: '1px solid #EF4444',
                        color: 'white'
                    }}>
                        <h3 style={{ color: 'white', marginBottom: '10px' }}>Error</h3>
                        <p>{error}</p>
                        <button 
                            onClick={loadData}
                            style={{
                                background: 'rgba(255, 255, 255, 0.2)',
                                border: '1px solid rgba(255, 255, 255, 0.3)',
                                color: 'white',
                                marginTop: '15px',
                                width: 'auto',
                                display: 'inline-block'
                            }}
                        >
                            Try Again
                        </button>
                    </div>
                )}

                <div className="card members-list">
                    <h2>Family Members</h2>
                    {family.length === 0 ? (
                        <p>No family members found yet. Send an invite or accept a pending request to get started!</p>
                    ) : (
                        <div>
                            <div style={{
                                marginBottom: '20px',
                                padding: '15px',
                                background: 'rgba(59, 130, 246, 0.1)',
                                borderRadius: '12px',
                                border: '1px solid rgba(59, 130, 246, 0.3)'
                            }}>
                                <p style={{ 
                                    margin: '0', 
                                    fontSize: '14px', 
                                    color: '#94A3B8' 
                                }}>
                                    Total Family Members: <strong style={{ color: '#3B82F6' }}>{family.length}</strong>
                                </p>
                            </div>

                            {family.map((member, idx) => (
                                <div className="member-card" key={idx}>
                                    <div style={{
                                        display: 'flex',
                                        justifyContent: 'space-between',
                                        alignItems: 'center',
                                        flexWrap: 'wrap',
                                        gap: '10px'
                                    }}>
                                        <p style={{ margin: '0' }}>
                                            <b>{member.name}</b> is your <i>{member.relationship_type || 'family member'}</i>
                                        </p>
                                        <div style={{
                                            display: 'flex',
                                            gap: '8px',
                                            flexWrap: 'wrap'
                                        }}>
                                            {member.email && (
                                                <span style={{
                                                    fontSize: '12px',
                                                    background: 'rgba(59, 130, 246, 0.2)',
                                                    color: '#60A5FA',
                                                    padding: '4px 8px',
                                                    borderRadius: '6px',
                                                    border: '1px solid rgba(59, 130, 246, 0.3)'
                                                }}>
                                                    {member.email}
                                                </span>
                                            )}
                                            {member.status && (
                                                <span style={{
                                                    fontSize: '12px',
                                                    background: member.status === 'active' 
                                                        ? 'rgba(16, 185, 129, 0.2)' 
                                                        : 'rgba(245, 158, 11, 0.2)',
                                                    color: member.status === 'active' 
                                                        ? '#10B981' 
                                                        : '#F59E0B',
                                                    padding: '4px 8px',
                                                    borderRadius: '6px',
                                                    border: `1px solid ${member.status === 'active' 
                                                        ? 'rgba(16, 185, 129, 0.3)' 
                                                        : 'rgba(245, 158, 11, 0.3)'}`
                                                }}>
                                                    {member.status}
                                                </span>
                                            )}
                                        </div>
                                    </div>
                                    {member.phone && (
                                        <p style={{ 
                                            margin: '8px 0 0 0', 
                                            fontSize: '14px', 
                                            color: '#94A3B8' 
                                        }}>
                                            <strong>Phone:</strong> {member.phone}
                                        </p>
                                    )}
                                </div>
                            ))}
                        </div>
                    )}
                </div>

                <div className="card actions-card">
                    <h3>Manage Connections</h3>
                    <p style={{ 
                        marginBottom: '25px', 
                        color: '#94A3B8', 
                        fontSize: '15px' 
                    }}>
                        Manage your family invitations and connections
                    </p>

                    <button onClick={handleViewInvitations}>
                        View Pending Invitations
                    </button>

                    <button onClick={handleSendInvite}>
                        Send Family Invite
                    </button>

                    <button 
                        onClick={loadData} 
                        style={{
                            background: 'linear-gradient(135deg, #6B7280, #4B5563)',
                            boxShadow: '0 4px 15px rgba(107, 114, 128, 0.3)'
                        }}
                    >
                        Refresh Family List 🔄
                    </button>

                    <button className="logout-btn" onClick={handleLogout}>
                        Logout
                    </button>
                </div>
            </div>
        </div>
    );
}
