import React, { useState } from 'react';

const LoginSignup = () => {
    const [action, setAction] = useState("Login");
    const [name, setName] = useState("");
    const [email, setEmail] = useState("");
    const [password, setPassword] = useState("");
    const [dob, setDob] = useState("");
    const [role, setRole] = useState("");
    const [medicalLicense, setMedicalLicense] = useState("");
    const [errorMsg, setErrorMsg] = useState("");
    const [isLoading, setIsLoading] = useState(false);

    // Mock API functions for demo
    const mockLogin = async (email, password) => {
        await new Promise(resolve => setTimeout(resolve, 1000));
        if (email === "demo@example.com" && password === "password") {
            return {
                access_token: "mock_token",
                role: "patient",
                user_id: "123"
            };
        }
        throw new Error("Invalid credentials");
    };

    const mockRegister = async (payload) => {
        await new Promise(resolve => setTimeout(resolve, 1000));
        return { success: true };
    };

    const handleLogin = async () => {
        setErrorMsg("");
        setIsLoading(true);
        try {
            const data = await mockLogin(email, password);
            // Mock success
            alert("Login successful!");
        } catch (err) {
            setErrorMsg(err.message);
        } finally {
            setIsLoading(false);
        }
    };

    const handleSignUp = async () => {
        setErrorMsg("");
        setIsLoading(true);

        if (!name || !email || !password || !dob || !role) {
            setErrorMsg("All fields are required");
            setIsLoading(false);
            return;
        }

        if (role === "doctor" && !medicalLicense) {
            setErrorMsg("Doctor registration requires a medical license number.");
            setIsLoading(false);
            return;
        }

        try {
            let payload = { name, email, password, date_of_birth: dob };
            if (role === "doctor") {
                payload.medical_license = medicalLicense;
            }

            await mockRegister(payload);
            alert("SignUp successful! Please login.");
            setAction("Login");
            // Clear form
            setName("");
            setEmail("");
            setPassword("");
            setDob("");
            setRole("");
            setMedicalLicense("");
        } catch (err) {
            setErrorMsg(`Registration failed: ${err.message}`);
        } finally {
            setIsLoading(false);
        }
    };

    const handleSubmit = () => {
        if (action === "Login") handleLogin();
        else handleSignUp();
    };

    const EmailIcon = () => (
        <svg className="w-6 h-6 fill-gray-400" viewBox="0 0 24 24">
            <path d="M20 4H4c-1.1 0-1.99.9-1.99 2L2 18c0 1.1.9 2 2 2h16c1.1 0 2-.9 2-2V6c0-1.1-.9-2-2-2zm0 4l-8 5-8-5V6l8 5 8-5v2z"/>
        </svg>
    );

    const PasswordIcon = () => (
        <svg className="w-6 h-6 fill-gray-400" viewBox="0 0 24 24">
            <path d="M18,8h-1V6c0-2.76-2.24-5-5-5S7,3.24,7,6v2H6c-1.1,0-2,0.9-2,2v10c0,1.1,0.9,2,2,2h12c1.1,0,2-0.9,2-2V10C20,8.9,19.1,8,18,8z M12,17c-1.1,0-2-0.9-2-2s0.9-2,2-2s2,0.9,2,2S13.1,17,12,17z M15.1,8H8.9V6c0-1.71,1.39-3.1,3.1-3.1s3.1,1.39,3.1,3.1V8z"/>
        </svg>
    );

    const UserIcon = () => (
        <svg className="w-6 h-6 fill-gray-400" viewBox="0 0 24 24">
            <path d="M12,4A4,4 0 0,1 16,8A4,4 0 0,1 12,12A4,4 0 0,1 8,8A4,4 0 0,1 12,4M12,14C16.42,14 20,15.79 20,18V20H4V18C4,15.79 7.58,14 12,14Z" />
        </svg>
    );

    const CalendarIcon = () => (
        <svg className="w-6 h-6 fill-gray-400" viewBox="0 0 24 24">
            <path d="M19,19H5V8H19M16,1V3H8V1H6V3H5C3.89,3 3,3.89 3,5V19A2,2 0 0,0 5,21H19A2,2 0 0,0 21,19V5C21,3.89 20.1,3 19,3H18V1M17,12H12V17H17V12Z" />
        </svg>
    );

    const CertificateIcon = () => (
        <svg className="w-6 h-6 fill-gray-400" viewBox="0 0 24 24">
            <path d="M13,21L15,20L13,19L14.5,17.5L12.5,16.5L14.5,14.5L12,13.5L14,12L12,10.5L13.5,9L12,7.5L13.5,6L12,4.5L13,3H11L10.5,1.5L9,3H7L6.5,4.5L5,6L6.5,7.5L5,9L6.5,10.5L5,12L7,13.5L4.5,14.5L6.5,16.5L4.5,17.5L6,19L4,20L6,21L7.5,22.5L9,21H11L12.5,22.5L13,21M12,18A6,6 0 0,1 6,12A6,6 0 0,1 12,6A6,6 0 0,1 18,12A6,6 0 0,1 12,18M12,8A4,4 0 0,0 8,12A4,4 0 0,0 12,16A4,4 0 0,0 16,12A4,4 0 0,0 12,8M12,10A2,2 0 0,1 14,12A2,2 0 0,1 12,14A2,2 0 0,1 10,12A2,2 0 0,1 12,10Z" />
        </svg>
    );

    return (
        <div className="min-h-screen bg-gradient-to-br from-gray-900 via-gray-800 to-gray-900 flex items-center justify-center p-4">
            <div className="bg-gray-800/50 backdrop-blur-sm border border-gray-700 rounded-2xl shadow-2xl w-full max-w-md p-8">
                {/* Header */}
                <div className="text-center mb-8">
                    <h1 className="text-4xl font-bold text-purple-400 mb-2">{action}</h1>
                    <div className="w-16 h-1.5 bg-gradient-to-r from-purple-500 to-pink-500 rounded-full mx-auto"></div>
                </div>

                {/* Form Inputs */}
                <div className="space-y-5">
                    {action === "SignUp" && (
                        <>
                            {/* Name Input */}
                            <div className="relative">
                                <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                                    <UserIcon />
                                </div>
                                <input
                                    type="text"
                                    placeholder="Full Name"
                                    value={name}
                                    onChange={(e) => setName(e.target.value)}
                                    className="w-full pl-14 pr-4 py-4 bg-gray-700/50 border border-gray-600 rounded-xl text-gray-100 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
                                />
                            </div>

                            {/* Date of Birth */}
                            <div className="relative">
                                <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                                    <CalendarIcon />
                                </div>
                                <input
                                    type="date"
                                    value={dob}
                                    onChange={(e) => setDob(e.target.value)}
                                    className="w-full pl-14 pr-4 py-4 bg-gray-700/50 border border-gray-600 rounded-xl text-gray-100 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
                                />
                            </div>

                            {/* Role Selection */}
                            <div className="relative">
                                <select
                                    value={role}
                                    onChange={(e) => setRole(e.target.value)}
                                    className="w-full px-4 py-4 bg-gray-700/50 border border-gray-600 rounded-xl text-gray-100 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all appearance-none cursor-pointer"
                                >
                                    <option value="" className="bg-gray-700">Select Role</option>
                                    <option value="patient" className="bg-gray-700">Patient</option>
                                    <option value="doctor" className="bg-gray-700">Doctor</option>
                                    <option value="family" className="bg-gray-700">Family Member</option>
                                </select>
                                <div className="absolute inset-y-0 right-0 pr-4 flex items-center pointer-events-none">
                                    <svg className="w-5 h-5 fill-gray-400" viewBox="0 0 20 20">
                                        <path d="M5.293 7.293a1 1 0 011.414 0L10 10.586l3.293-3.293a1 1 0 111.414 1.414l-4 4a1 1 0 01-1.414 0l-4-4a1 1 0 010-1.414z" />
                                    </svg>
                                </div>
                            </div>

                            {/* Medical License (Doctor only) */}
                            {role === "doctor" && (
                                <div className="relative">
                                    <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                                        <CertificateIcon />
                                    </div>
                                    <input
                                        type="text"
                                        placeholder="Medical License Number"
                                        value={medicalLicense}
                                        onChange={(e) => setMedicalLicense(e.target.value)}
                                        className="w-full pl-14 pr-4 py-4 bg-gray-700/50 border border-gray-600 rounded-xl text-gray-100 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
                                    />
                                </div>
                            )}
                        </>
                    )}

                    {/* Email Input */}
                    <div className="relative">
                        <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                            <EmailIcon />
                        </div>
                        <input
                            type="email"
                            placeholder="Email address"
                            value={email}
                            onChange={(e) => setEmail(e.target.value)}
                            className="w-full pl-14 pr-4 py-4 bg-gray-700/50 border border-gray-600 rounded-xl text-gray-100 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
                        />
                    </div>

                    {/* Password Input */}
                    <div className="relative">
                        <div className="absolute inset-y-0 left-0 pl-4 flex items-center pointer-events-none">
                            <PasswordIcon />
                        </div>
                        <input
                            type="password"
                            placeholder="Password"
                            value={password}
                            onChange={(e) => setPassword(e.target.value)}
                            className="w-full pl-14 pr-4 py-4 bg-gray-700/50 border border-gray-600 rounded-xl text-gray-100 placeholder-gray-400 focus:outline-none focus:ring-2 focus:ring-purple-500 focus:border-transparent transition-all"
                        />
                    </div>
                </div>

                {/* Forgot Password (Login only) */}
                {action === "Login" && (
                    <div className="text-center mt-6">
                        <span className="text-gray-400 text-sm">
                            Lost Password? 
                            <button className="text-purple-400 hover:text-purple-300 transition-colors ml-1 underline">
                                Click Here
                            </button>
                        </span>
                    </div>
                )}

                {/* Error Message */}
                {errorMsg && (
                    <div className="mt-4 p-3 bg-red-900/30 border border-red-500/50 rounded-lg text-red-300 text-sm text-center">
                        {errorMsg}
                    </div>
                )}

                {/* Switch Action */}
                <div className="text-center mt-6">
                    <span className="text-gray-400 text-sm">
                        {action === "Login" ? "Don't have an account? " : "Already have an account? "}
                        <button
                            onClick={() => {
                                setAction(action === "Login" ? "SignUp" : "Login");
                                setErrorMsg("");
                            }}
                            className="text-purple-400 hover:text-purple-300 transition-colors underline font-medium"
                        >
                            {action === "Login" ? "SignUp" : "Login"}
                        </button>
                    </span>
                </div>

                {/* Submit Button */}
                <button
                    onClick={handleSubmit}
                    disabled={isLoading}
                    className="w-full mt-8 py-4 bg-gradient-to-r from-purple-600 to-pink-600 hover:from-purple-700 hover:to-pink-700 disabled:from-gray-600 disabled:to-gray-600 text-white font-bold text-lg rounded-xl transition-all duration-300 transform hover:scale-[1.02] active:scale-[0.98] shadow-lg hover:shadow-xl disabled:cursor-not-allowed"
                >
                    {isLoading ? (
                        <div className="flex items-center justify-center">
                            <svg className="animate-spin -ml-1 mr-3 h-5 w-5 text-white" xmlns="http://www.w3.org/2000/svg" fill="none" viewBox="0 0 24 24">
                                <circle className="opacity-25" cx="12" cy="12" r="10" stroke="currentColor" strokeWidth="4"></circle>
                                <path className="opacity-75" fill="currentColor" d="M4 12a8 8 0 018-8V0C5.373 0 0 5.373 0 12h4zm2 5.291A7.962 7.962 0 014 12H0c0 3.042 1.135 5.824 3 7.938l3-2.647z"></path>
                            </svg>
                            Processing...
                        </div>
                    ) : action}
                </button>

                {/* Demo Credentials */}
                {action === "Login" && (
                    <div className="mt-6 p-3 bg-blue-900/20 border border-blue-500/30 rounded-lg text-blue-300 text-xs text-center">
                        <p className="font-medium mb-1">Demo Credentials:</p>
                        <p>Email: demo@example.com</p>
                        <p>Password: password</p>
                    </div>
                )}
            </div>
        </div>
    );
};

export default LoginSignup;
